INSERT INTO `products` (`id`, `Title`, `thumb`, `images`, `cate_id`, `description`, `content`, `thongsokythuat`, `price`, `discount`, `slug`, `active`, `ishot`, `isnewfeed`, `Amounts`, `types`, `count_buy`, `created_at`, `updated_at`) VALUES
(40, 'Áo thun nữ ngắn tay cổ tròn dáng ôm', 'ao-thun-nu-ngan-tay-co-tron-dang-om-1763394338-691b432284ec3.jpg', '[\"ao-thun-nu-ngan-tay-co-tron-dang-om-1763394338-691b432284ec3.jpg\", \"ao-thun-nu-ngan-tay-co-tron-dang-om-1763394338-691b432285be4.jpg\", \"ao-thun-nu-ngan-tay-co-tron-dang-om-1763394338-691b43228602b.jpg\", \"ao-thun-nu-ngan-tay-co-tron-dang-om-1763394338-691b43228651e.jpg\"]', 9, '<h1>&Aacute;o thun nữ ngắn tay cổ tr&ograve;n d&aacute;ng &ocirc;m</h1>', '<h3>Th&ocirc;ng tin sản phẩm:</h3>\r\n\r\n<ul>\r\n	<li>Kiểu d&aacute;ng: Slim fit</li>\r\n	<li>M&agrave;u sắc: Hồng - T&iacute;m than - Xanh</li>\r\n	<li>Th&ocirc;ng số size: S - M - L - XL - XXL</li>\r\n	<li>Chất liệu vải: Sợi tổng hợp</li>\r\n	<li>Th&agrave;nh phần:&nbsp;88% Polyester 12% Spandex</li>\r\n</ul>\r\n\r\n<p><img src=\"https://file.hstatic.net/200000471735/file/chi-tiet-san-pham-ao-phong-nu_b7aac89fcb964c7aa7cf6c8a118fcffe_grande.png\" /></p>\r\n\r\n<h3>Đặc điểm nổi bật:</h3>\r\n\r\n<p>-&nbsp;<a href=\"https://savani.vn/collections/ao-t-shirt-nu\"><strong>&Aacute;o ph&ocirc;ng&nbsp;nữ</strong></a>&nbsp;với thiết kế đơn giản, đa năng, dễ mặc&nbsp;phom d&aacute;ng vừa vặn ph&ugrave; hợp nhiều d&aacute;ng người.</p>\r\n\r\n<p><strong>-&nbsp;</strong>Form Slim fit &ocirc;m s&aacute;t, mang lại vẻ ngo&agrave;i gọn g&agrave;ng t&ocirc;n l&ecirc;n v&oacute;c d&aacute;ng nhưng vẫn đảm bảo sự thoải m&aacute;i khi vận động</p>\r\n\r\n<p>- M&agrave;u sắc thanh lịch, dễ kết hợp với&nbsp;<strong><a href=\"https://savani.vn/collections/chan-vay-nu\">ch&acirc;n v&aacute;y</a></strong>,&nbsp;<a href=\"https://savani.vn/collections/quan-jean-nu\"><strong>quần jean</strong></a>,&nbsp;<a href=\"https://savani.vn/collections/quan-short-nu\"><strong>quần short</strong></a>&hellip;&Aacute;o ph&ugrave; hợp mặc đi đến nhiều nơi, nhiều ho&agrave;n cảnh kh&aacute;c nhau như đi l&agrave;m, đi chơi hay đi tiệc.</p>\r\n\r\n<p><img src=\"https://file.hstatic.net/200000471735/file/dac-diem-noi-bat-cua-ao-phong-nu_b4feddca5a4046a2bdd95c9e70f599e9_grande.png\" /></p>\r\n\r\n<p>Chất liệu:</p>\r\n\r\n<p>&Aacute;o ph&ocirc;ng nữ&nbsp;l&agrave; một trong những sản phẩm ti&ecirc;n phong được Savani đặc biệt ch&uacute; trọng về chất liệu. Sự kết hợp giữa&nbsp;Polyester (88%) v&agrave;&nbsp;Spandex (12%) gi&uacute;p sản phẩm c&oacute; c&aacute;c ưu điểm như: Độ bền cao, chống nhăn, nhanh kh&ocirc; v&agrave; giữ m&agrave;u tốt,&nbsp;co gi&atilde;n tốt, &ocirc;m cơ thể m&agrave; vẫn thoải m&aacute;i.</p>\r\n\r\n<ul>\r\n	<li><strong>Bền -&nbsp;kh&ocirc;ng nhăn -&nbsp;giữ d&aacute;ng tốt:&nbsp;</strong>Kh&ocirc;ng&nbsp;nhăn, kh&ocirc;ng co r&uacute;t,&nbsp;kh&ocirc;ng bai d&atilde;o giữ phom tốt sau nhiều lần mặc v&agrave; giặt&nbsp;</li>\r\n	<li><strong>Nhanh kh&ocirc; -&nbsp;&iacute;t thấm nước:</strong>&nbsp;Polyester &iacute;t h&uacute;t ẩm n&ecirc;n kh&ocirc; rất nhanh sau khi giặt hoặc thấm mồ h&ocirc;i, rất ph&ugrave; hợp với thời tiết ẩm hoặc người hay vận động.</li>\r\n	<li><strong>Co gi&atilde;n tốt&nbsp;-&nbsp;linh hoạt vận động:</strong>&nbsp;12% Spandex gi&uacute;p vải co gi&atilde;n 4 chiều, kh&ocirc;ng bị cứng khi mặc &ocirc;m cơ thể hay vận động nhiều.</li>\r\n	<li><strong>Giữ m&agrave;u tốt -&nbsp;kh&oacute; phai:</strong>&nbsp;Polyester b&aacute;m m&agrave;u tốt, hạn chế bay m&agrave;u sau thời gian d&agrave;i sử dụng.</li>\r\n</ul>\r\n\r\n<p>&Aacute;o ph&ocirc;ng nữ&nbsp;được xem l&agrave; một m&oacute;n đồ m&agrave; bất k&igrave; ai cũng n&ecirc;n sở hữu cho ri&ecirc;ng m&igrave;nh. Bởi &aacute;o ph&ocirc;ng&nbsp;l&agrave; m&oacute;n đồ đa năng -&nbsp;dễ phối -&nbsp;kh&ocirc;ng k&eacute;n d&aacute;ng&nbsp;chỉ cần thay đổi một ch&uacute;t&nbsp;l&agrave; đ&atilde; c&oacute; một outfit mới mẻ, dễ mặc v&agrave; cực kỳ linh hoạt&nbsp;ph&ugrave; hợp với nhiều phong c&aacute;ch từ năng động, trẻ trung đến lịch sự, tinh gọn.</p>\r\n\r\n<p><img src=\"https://file.hstatic.net/200000471735/file/cac-uu-diem-cua-ao-phong-nu_4cb82849298e4e1e91b0a50c5a646d0a_grande.png\" /></p>\r\n\r\n<h3>C&aacute;ch bảo quản &aacute;o ph&ocirc;ng đ&uacute;ng c&aacute;ch:</h3>\r\n\r\n<p>Mọi trang phục đều c&oacute; tuổi thọ nhất định. Nhưng nếu biết c&aacute;ch bạn c&oacute; thể giữ được form d&aacute;ng chuẩn v&agrave; k&eacute;o d&agrave;i được thời gian sử dụng&nbsp;l&acirc;u hơn.</p>\r\n\r\n<ol>\r\n	<li>Ph&acirc;n loại trước khi giặt.</li>\r\n	<li>Chọn chế độ giặt nhẹ đối với m&aacute;y giặt</li>\r\n	<li>Lộn tr&aacute;i sản phẩm để giặt v&agrave; phơi</li>\r\n	<li>Sử dụng nước lạnh để giặt, kh&ocirc;ng ng&acirc;m l&acirc;u trong h&oacute;a chất.</li>\r\n	<li>D&ugrave;ng m&oacute;c để phơi đồ</li>\r\n</ol>\r\n\r\n<h3>Ch&iacute;nh s&aacute;ch đổi trả:</h3>\r\n\r\n<p>- &Aacute;p dụng đổi h&agrave;ng trong c&aacute;c trường hợp sản phẩm kh&aacute;ch mua kh&ocirc;ng hợp size, số, m&agrave;u sắc, kiểu d&aacute;ng<br />\r\n- Sản phẩm c&ograve;n nguy&ecirc;n vẹn, nguy&ecirc;n tem m&aacute;c, chưa giặt, chưa qua sử dụng v&agrave; sửa chữa<br />\r\n- Sản phẩm mua kh&ocirc;ng nằm trong nh&oacute;m sản phẩm sale sốc<br />\r\n- Gi&aacute; trị sản phẩm đổi: bằng hoặc cao hơn gi&aacute; trị sản phẩm đ&atilde; mua trước đ&oacute;</p>\r\n\r\n<p><img src=\"https://file.hstatic.net/200000471735/file/chinh-sach-mua-hang-tai-savani_bbe71f8f54f24c578da4c79c9e8834fb_grande.png\" /></p>\r\n\r\n<h3>Ch&iacute;nh s&aacute;ch bảo h&agrave;nh:</h3>\r\n\r\n<ul>\r\n	<li>Kh&aacute;ch h&agrave;ng được sữa chữa miễn ph&iacute; trong trường hợp sản phẩm lỗi từ ph&iacute;a nh&agrave; sản xuất: đứt chỉ, bong chỉ, hư kho&aacute;, mất c&uacute;c, l&agrave; phẳng</li>\r\n	<li>Đổi mới sản phẩm nếu sản phẩm kh&aacute;ch mua bị r&aacute;ch vải, x&ugrave; vải do lỗi nh&agrave; sản xuất</li>\r\n	<li>Đổi mới sản phẩm nếu sản phẩm kh&aacute;ch mua bị ra m&agrave;u nhiều dẫn tới bạc vải, phai m&agrave;u sang m&agrave;u phối c&ugrave;ng (đối với quần/&aacute;o phối m&agrave;u)</li>\r\n	<li>Đối với bộ đồ (bộ nỉ, bộ gi&oacute;) nếu sản phẩm bị lỗi &aacute;o hoặc quần th&igrave; sẽ bảo h&agrave;nh theo sản phẩm &aacute;o hoặc quần bị lỗi chứ kh&ocirc;ng bảo h&agrave;nh nguy&ecirc;n bộ.</li>\r\n</ul>\r\n\r\n<h3>QUY ĐỊNH VỀ PH&Iacute; VẬN CHUYỂN ĐỐI VỚI ĐƠN H&Agrave;NG ONLINE</h3>\r\n\r\n<p>-&nbsp;<a href=\"https://savani.vn/\"><strong>Savani</strong></a>&nbsp;hỗ trợ ph&iacute; đổi h&agrave;ng 2 chiều cho c&aacute;c đơn h&agrave;ng online trong trường hợp: h&agrave;ng bị lỗi từ nh&agrave; sản xuất, giao kh&ocirc;ng đ&uacute;ng mẫu hoặc kh&ocirc;ng đ&uacute;ng size kh&aacute;ch y&ecirc;u cầu.<br />\r\n- Free ship với tất cả c&aacute;c đơn h&agrave;ng c&oacute; gi&aacute; trị từ 500.000đ trở l&ecirc;n.<br />\r\n- Hỗ trợ đổi h&agrave;ng trong v&ograve;ng 30 ng&agrave;y v&agrave; miễn ph&iacute; tại tất cả chi nh&aacute;nh thuộc hệ thống thời trang Savani.</p>', NULL, '200000', '189000', 'ao-thun-nu-ngan-tay-co-tron-dang-om', 0x31, 0x31, 0x31, '100', 'S, M, L, XL, XXL', NULL, '2025-11-17 08:45:38', '2025-11-17 08:45:38'),
(41, 'Áo gió có mũ nữ phối tay thân in logo trước ngực', 'ao-gio-co-mu-nu-phoi-tay-than-in-logo-truoc-nguc-1763396524-691b4bac85656.jpg', '[\"ao-gio-co-mu-nu-phoi-tay-than-in-logo-truoc-nguc-1763396524-691b4bac85656.jpg\", \"ao-gio-co-mu-nu-phoi-tay-than-in-logo-truoc-nguc-1763396524-691b4bac85fb3.jpg\", \"ao-gio-co-mu-nu-phoi-tay-than-in-logo-truoc-nguc-1763396524-691b4bac8650f.jpg\", \"ao-gio-co-mu-nu-phoi-tay-than-in-logo-truoc-nguc-1763396524-691b4bac86a8f.jpg\"]', 9, '<h1>&Aacute;o gi&oacute; c&oacute; mũ nữ phối tay th&acirc;n in logo trước ngực</h1>', '<h3>Th&ocirc;ng tin sản phẩm:</h3>\r\n\r\n<ul>\r\n	<li>Kiểu d&aacute;ng: Regular fit (Form su&ocirc;ng vừa)</li>\r\n	<li>M&agrave;u sắc: Be - X&aacute;m - T&iacute;m than</li>\r\n	<li>Th&ocirc;ng số size: S - M - L - XL -&nbsp;&nbsp;XXL</li>\r\n	<li>Chất liệu vải: Sợi tổng hợp</li>\r\n	<li>Th&agrave;nh phần:&nbsp;100% Polyester</li>\r\n</ul>\r\n\r\n<p><img src=\"https://cdn.hstatic.net/files/200000471735/file/chi-tiet-san-pham-ao-po-lo-nam_d5724c922567459f963a97e86659dfd0_grande.png\" /></p>\r\n\r\n<h3>Đặc điểm nổi bật:</h3>\r\n\r\n<p>-&nbsp;<a href=\"https://savani.vn/collections/ao-gio-nu\"><strong>&Aacute;o gi&oacute;&nbsp;nữ</strong></a>&nbsp;với thiết kế đơn giản, đa năng, dễ mặc&nbsp;phom d&aacute;ng vừa vặn ph&ugrave; hợp nhiều d&aacute;ng người.</p>\r\n\r\n<p><strong>-&nbsp;</strong>Form Regular&nbsp;fit su&ocirc;ng&nbsp;&nbsp;vừa, mang lại vẻ ngo&agrave;i gọn g&agrave;ng, nữ t&iacute;nh, giữ nhiệt tốt gi&uacute;p cơ thể lu&ocirc;n ấm &aacute;p trong trời thu đ&ocirc;ng</p>\r\n\r\n<p>- M&agrave;u sắc thanh lịch, dễ kết hợp với&nbsp;<a href=\"https://savani.vn/collections/chan-vay-nu\"><strong>ch&acirc;n v&aacute;y</strong></a>,&nbsp;<strong><a href=\"https://savani.vn/collections/quan-ni-nu\">quần nỉ</a></strong>,&nbsp;<a href=\"https://savani.vn/collections/quan-jean-nu\"><strong>quần jean</strong></a>&hellip;&Aacute;o ph&ugrave; hợp mặc ở nh&agrave;,&nbsp;đi l&agrave;m, đi chơi,...</p>\r\n\r\n<p><img src=\"https://cdn.hstatic.net/files/200000471735/file/dac-diem-noi-bat-cua-ao-po-lo-nam_6d86dde60a8f423b848156094209a532_grande.png\" /></p>\r\n\r\n<p>Chất liệu:</p>\r\n\r\n<p>&Aacute;o gi&oacute; nữ l&agrave; một trong những sản phẩm ti&ecirc;n phong được Savani đặc biệt ch&uacute; trọng về chất liệu. Được sử dụng ho&agrave;n to&agrave;n bằng&nbsp;sợi tổng hợp&nbsp;Polyester (100%) gi&uacute;p sản phẩm c&oacute; c&aacute;c&nbsp;ưu điểm như: Độ bền cao, chống nhăn, nhanh kh&ocirc; v&agrave; giữ m&agrave;u tốt</p>\r\n\r\n<ul>\r\n	<li><strong>Bền -&nbsp;kh&ocirc;ng nhăn -&nbsp;giữ d&aacute;ng tốt:&nbsp;</strong>Kh&ocirc;ng&nbsp;nhăn, kh&ocirc;ng co r&uacute;t,&nbsp;kh&ocirc;ng bai d&atilde;o giữ phom tốt sau nhiều lần mặc v&agrave; giặt&nbsp;</li>\r\n	<li><strong>Nhanh kh&ocirc; -&nbsp;&iacute;t thấm nước:</strong>&nbsp;Polyester &iacute;t h&uacute;t ẩm n&ecirc;n kh&ocirc; rất nhanh sau khi giặt&nbsp;</li>\r\n	<li><strong>Giữ m&agrave;u tốt -&nbsp;kh&oacute; phai:</strong>&nbsp;Polyester b&aacute;m m&agrave;u tốt, hạn chế bay m&agrave;u sau thời gian d&agrave;i sử dụng.</li>\r\n</ul>\r\n\r\n<p>&Aacute;o gi&oacute; nữ được xem l&agrave; một m&oacute;n đồ đa di năng m&agrave; bất k&igrave; ai cũng n&ecirc;n sở hữu cho ri&ecirc;ng m&igrave;nh. &Aacute;o&nbsp;gi&oacute; l&agrave; sản phẩm v&ocirc; c&ugrave;ng cần thiết trong thời điểm thu đ&ocirc;ng v&igrave; &aacute;o gi&oacute; giữ nhiệt tốt, gi&uacute;p cơ thể lu&ocirc;n ấm &aacute;p trong thời tiết&nbsp;lạnh. Đồng thời, cấu tr&uacute;c sợi dệt tho&aacute;ng kh&iacute; gi&uacute;p hạn chế hầm b&iacute; v&agrave; t&iacute;ch nhiệt, mang lại cảm gi&aacute;c dễ chịu khi mặc cả ng&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"https://cdn.hstatic.net/files/200000471735/file/cac-uu-diem-cua-ao-po-lo-nam_f56413d2ebac4dcfa6a09279bbfc28df_grande.png\" /></p>\r\n\r\n<h3>C&aacute;ch bảo quản &aacute;o gi&oacute; nữ đ&uacute;ng c&aacute;ch:</h3>\r\n\r\n<p>Mọi trang phục đều c&oacute; tuổi thọ nhất định. Nhưng nếu biết c&